import React from "react";
import styled from "styled-components";
import FontAwesomeIcon from '@fortawesome/react-fontawesome'
// import {PaperPlane}
const Home = () => {
  return (
    <>
      <HomeWrapper>
          <SideMenu>
                    <h1>Chat</h1>
                    <SideMenuBtn>
                              + New chat
                    </SideMenuBtn>
          </SideMenu>
          <ChatBox>
                    <h1>Chat With Jarvis</h1>
                    <ChatInputHolder>
                              <ChatInput
                                        placeholder="Type your chat here"
                              />
                    </ChatInputHolder>
          </ChatBox>
      </HomeWrapper>
    </>
  );
};

const HomeWrapper = styled.div`
  width: 100vw;
  height: 100vh;
  background-color: rgba(52, 53, 65, 1);
  display: flex;
`;

const SideMenu = styled.aside`
  width: 25vw;
  height: 100vh;
  padding: 30px;
  background-color: #202123;
  color: #fff;
`;

const SideMenuBtn =styled.div`
          padding: 12px;
          border: 1px solid white;
          border-radius: 5px;
          text-align: left;
          transition: all 0.25s ease;
          &:hover{
                    background-color: rgba(255,255,255,0.1);
                    cursor: pointer;
          }
`

const ChatBox = styled.div`
color: #fff;
          flex: 1;
          background-color: #343541;
          position: relative;
`

const ChatInputHolder = styled.div`
        display  : flex;
        width: 100%;
        padding: 12px;
        position: absolute;
        bottom: 0;
        left: 0;
        right: 0;
`

const ChatInput = styled.textarea`
          background-color: #40414f;
          color: #fff;
          width: 90%;
          border-radius: 5px;
          border: none;
          outline: none;
          box-shadow: 0 0 8px 0 rgba(0,0,0,0.5);
          resize: none;
          font-size: 1.25em;
          overflow-y: hidden;
          max-height: 200px;
          height: 50px;
          margin: 10px;
`
export default Home;
